import express from 'express';
import jwt from 'jsonwebtoken';
import userInfo from '../models/users';
import {} from 'dotenv/config';
import {createError} from 'http-errors';
import { v4 as uuidv4 } from 'uuid';

const {SECRET_KEY} = process.env;


export const  login=(req,res)=>{

    let users=[];
    req.auth = false;

    for (let user of userInfo) {
        user = { id: uuidv4(), ...users };
        users.push(user);
    }

    const {email ,password} = req.headers;
    if (email && password) {
        for (let user of users) {
            if (email === user.email && password === user.password) {
                req.auth = true;
                try {
                    jwt.sign({ username: user.username, email, password, role: user.role },SECRET_KEY, (err, token) => {
                        if(token) {
                            req.auth = true;
                            req.token = token;
                            return res.status(200).json({ token });
                        }
                        else if(err)
                            return res.status(err.status).json({
                                status: err.status,
                                message: err.message
                            })
                        else
                            return res.status(501).json({
                                status: 501,
                                message: "Unknown error"
                            });
                    })
                } catch (err) {
                    return res.json({
                        status: err.status,
                        message: err.message
                    })
                }
            }
        }
    } else {
        return res.status(400).json({
            status: 'Bad Request',
            message: 'You need to supply the email and password'
        })
    }
    if(!req.auth) {
        return res.status(401).json({
            status: 'Unauthorized',
            message: 'Unable to find user with the provided email / password combination'
        });
    }
}
